CREATE TABLE AppUser (    id          INTEGER PRIMARY KEY AUTOINCREMENT,    name   	VARCHAR,    surname   	VARCHAR,    age		INTEGER,    gender	VARCHAR,    tc		NUMERIC,    sp02	NUMERIC,    heartRate   NUMERIC,    temperature NUMERIC,    systolicBP  NUMERIC,    diastolicBP NUMERIC,    role 	VARCHAR,    date	VARCHAR,    time	VARCHAR);CREATE TABLE LoginTable (    id          INTEGER PRIMARY KEY AUTOINCREMENT,    name   	VARCHAR,    password	VARCHAR);